-- 新テーブルの作成create table public.m_companies (  name varchar(100) not null  , post_code varchar(100) not null  , address_1 varchar(100) not null  , address_2 varchar(100)  , tel varchar(100) not null  , fax varchar(100))/-- 主キーの作成alter table public.m_companies  add primary key (name)/-- コメントの作成comment on table public.m_companies is '会社情報マスタ'/comment on column public.m_companies.name is ''/comment on column public.m_companies.post_code is ''/comment on column public.m_companies.address_1 is ''/comment on column public.m_companies.address_2 is ''/comment on column public.m_companies.tel is ''/comment on column public.m_companies.fax is ''/